        //       center: window.ol.proj.fromLonLat([center.lng, center.lat]),
        //       zoom: zoom
        //     })
        // });

    }, [center, zoom]);   



    return (
        <div 
            ref={mapRef} 
            className={`map ${props.className}`} 
            style={props.style}
            // id="map"
        ></div>
    );
};

export default Map;
