import React, { useReducer } from "react";

import './Input.css';

import { validate } from "../../util/validators";

const inputReducer = (state, action) => {
    switch(action.type) {
        case 'CHANGE':
            return {
                ...state,
                value: action.val,
                isValid: validate(action.val, action.validators)
            };
        case 'TOUCH': {
            return {
                ...state,
                isTouched: true
            }
        }
        default:
            return state;
    }
};

const Input = props => {
    const [inputeState, dispatch] = useReducer(inputReducer, {value: '', isValid: false, isTouched: false});

    const changeHandler = event => {
        dispatch({
            type: 'CHANGE', 
            val: event.target.value, 
            validators: props.validators 
        });
    };

    const touchHandler = () => {
        dispatch({
            type: 'TOUCH'
        });
    };

    const element = props.element === 'input' ? (
        <input 
            id={props.id} 
            type={props.type} 
            placeholder={props.placeholder} 
            onChange={changeHandler}
            onBlur={touchHandler}
            value={inputeState.value}
        />
    ) : (
        <textarea 
            id={props.id} 
            rows={props.rows || 3} 
            onChange={changeHandler}
            onBlur={touchHandler}
            value={inputeState.value}
        />
    );

    return (
        <div className={`form-control ${!inputeState.isValid && inputeState.isTouched && 'form-control--invalid'}`}>
            <label htmlFor={props.id}>{props.label}</label>
            {element}
            {!inputeState.isValid && inputeState.isTouched && <p>{props.errorText}</p>}
        </div> 
    )
};

export default Input;
